INSERT INTO `faq` (`id_faq`, `pertanyaan`, `jawaban`, `urutan`) VALUES
(4, 'Apa itu BAZNAS?', 'Badan Amil Zakat Nasional (BAZNAS) merupakan badan resmi dan satu-satunya yang dibentuk oleh pemerintah berdasarkan Keputusan Presiden RI No. 8 Tahun 2001 yang memiliki tugas dan fungsi menghimpun dan menyalurkan zakat, infaq, dan sedekah (ZIS) pada tingkat nasional.', 1),
(5, 'Undang-undang berdirinya Baznas ?', 'Lahirnya Undang-Undang Nomor 23 Tahun 2011 tentang Pengelolaan Zakat semakin mengukuhkan peran BAZNAS sebagai lembaga yang berwenang melakukan pengelolaan zakat secara nasional. Dalam UU tersebut, BAZNAS dinyatakan sebagai lembaga pemerintah nonstruktural yang bersifat mandiri dan bertanggung jawab kepada Presiden melalui Menteri Agama.', 2),
(6, 'Bagaimana cara membayar Zakat, Infaq dan Sedekah (ZIS) di BAZNAS Kota Tasikmalaya?', 'Ada 2 opsi yang dapat digunakan : Pertama, langsung datang ke counter BAZNAS Kota Tasikmalaya. Kedua, via rekening yang tersedia di BAZNAS, kemudianmasuk pada halaman \"Konfirmasi Bayar\" ', 3),
(7, 'Bagaimana cara saya mengajukan permohonan bantuan?', 'Untuk mengajukan permohonan bantuan : Pertama, anda harus melengkapi terlebih dahulu persyaratan administrasi yang terdiri dari fotokopi identitas diri (KK/KTP), Surat permohonan dari kelurahan, Surat Keterangan Tidak Mampu (SKTM), dokumen pendukung (misal untuk bantuan kesehatan, diperlukan surat rujukkan), dan foto calon penerima manfaat. Kedua, setelah lengkap, kirimkan berkas tersebut ke sekretariat BAZNAS Kota Tasikmalaya (Divisi Pendistribusian dan Pendayagunaan)', 4),
(8, 'Bagaimana cara saya mengetahui informasi lebih lanjut terkait permohonan yang sudah diajukan?', 'Terkait permohonan yang sudah diajukan, maka akan difollow up oleh bagian terkait', 5),
(11, 'Adakah peraturan tentang pengajuan permohonan rekomendasi izin pembentukan dan pembukaan perwakilan Lembaga Amil Zakat (LAZ)?', ' Ada. Peraturan Badan Amil Zakat Nasional Republik Indonesia Nomor 3 Tahun 2019 Tentang Tata Cara Permohonan Rekomendasi Izin Pembentukan Dan Pembukaan Perwakilan Lembaga Amil Zakat (LAZ). Filenya dapat diakses di menu \"Download\"', 6),
(12, 'Bagaimana cara mengajukan permohonan penelitian?', 'Ada. Peraturan Badan Amil Zakat Nasional Republik Indonesia Nomor 3 Tahun 2019 Tentang Tata Cara Permohonan Rekomendasi Izin Pembentukan Dan Pembukaan Perwakilan Lembaga Amil Zakat (LAZ). Filenya dapat diakses di menu \"Download\"', 7),
(13, 'Fatwa MUI tentang Zakat penghasilan?', ' Fatwa MUI Nomor 3 tahun 2003 tentang Zakat Penghasilan', 8);
